def Find_the_greatest_product(num_list):
    max = int(num_list[0:5])
    times = len(num_list) - 5
    for i in range(0, times):
        if max < int(num_list[i:i + 5]):
            max = int(num_list[i:i + 5])
    print
    'the greatest product of five consecutive digits is {0}'.format(max)
    return max


